if (Object.keys(K('form').validate()).length>0) {
  K('form').submitPage();
} else {
    K('section[Confirmation]').show();
  	confirmationInfo=bundle.confirmationInfoHTML;
    confirmationInfo=confirmationInfo.replaceAll("_MemberName_", K('field[Student First Name]').value()+" "+K('field[Student Last Name]').value());

  	var startDate=moment(K('field[Date of Last Payment]').value(), "YYYY-MM-DD").format("Do MMM YYYY");
